INSERT [dbo].[Product]  (id, [name], [image], price, title, [description], cateID,quantity) VALUES ('P001',N'Thức ăn cho chó Becgie MKB All Life Stages Formula Nutrition','https://www.petmart.vn/wp-content/uploads/2020/11/thuc-an-cho-cho-becgie-mkb-all-life-stages-formula-nutrition.jpg','280000',N'Thức ăn cho chó Becgie MKB All Life Stages Formula Nutrition phù hợp với tất cả các giai đoạn phát triển của chó cưng',N'Thức ăn cho chó Becgie MKB All Life Stages Formula Nutrition được sản xuất dựa trên các nghiên cứu về đặc điểm xương hàm, cấu trúc miệng, thói quen nuốt và tốc độ ăn của chó cưng. Thức ăn dành riêng cho chó Becgie tái tạo hoàn toàn sức khỏe của thú cưng. Mang lại nhiều lợi ích to lớn như: Chăm sóc sức khỏe của cún cưng với công thức dinh dưỡng độc quyền. Chăm sóc da và lông cún cưng khỏe mạnh. Thành phần dinh dưỡng từ thịt cá, 23 loại axit béo (EPA và DHA) giúp lông chó mềm mượt, làn da khỏe mạnh. Cơ thể khỏe mạnh hỗ trợ tăng trưởng, hàm lượng Protein và Axit Amin được điều chỉnh khoa học, L-carnitine giúp tăng trưởng và phát triển thể chất. Kiểm soát độ nhạy cảm hệ tiêu hóa và hạn chế mùi phân.','C001',300);

INSERT [dbo].[Product]  (id, [name], [image], price, title, [description], cateID,quantity) VALUES ('P002',N'Xương cho chó vị thịt cừu và sữa VEGEBRAND Orgo Milk And Mutton Stick','https://www.petmart.vn/wp-content/uploads/2019/04/xuong-cho-cho-vi-thit-cuu-va-sua-vegebrand-orgo-milk-and-mutton-stick.jpg','85000',N'Xương cho chó vị thịt cừu và sữa VEGEBRAND Orgo Milk And Mutton Stick là sản phẩm dinh dưỡng dành cho tất cả các giống chó.',N'Xương cho chó vị thịt cừu và sữa VEGEBRAND Orgo Milk And Mutton Stick với thành phần từ thiên nhiên, không chứa chất bảo quản. Giúp cho hương vị trở nên thơm ngon hơn. Sữa bổ sung nhiều canxi, giúp xương chắc khỏe hơn. Xương gặm cho chó giúp làm sạch răng, loại bỏ 99% những mảng cao răng cứng đầu. Hơn nữa sản phẩm còn thúc đẩy hệ tiêu hóa, tránh những bệnh về đường ruột. Làm giảm mùi hôi miệng cho thú cưng. Sữa và thịt cừu ngon và dai, tốt cho răng và không dính vào răng. Chăm sóc lông và da luôn khỏe mạnh.','C001',300)

INSERT [dbo].[Product]  (id, [name], [image], price, title, [description], cateID,quantity) VALUES ('P003',N'Sữa tắm cho chó trị ve rận BBN Killing Mites Shampoo','https://www.petmart.vn/wp-content/uploads/2016/09/sua-tam-cho-cho-tri-ve-ran-bbn-killing-mites-shampoo.jpg','150000',N'Sữa tắm cho chó trị ve rận BBN Killing Mites Shampoo dành cho tất cả các giống chó.',N'Sữa tắm cho chó trị ve rận BBN Killing Mites Shampoo đặc biệt tốt cho những chú chó bị ve và nấm. Giúp cơ thể cún cưng trút bỏ các tế bào da sần sùi hoặc tế bào chết. Dầu tự nhiên 100% giúp làm sạch sâu và giữ ẩm cho lông và da chó của bạn. Giảm men, nguồn thức ăn của ve tối đa.Tất cả dầu gội tự nhiên và hữu cơ Pet Wash, công thức đặc biệt dành cho da khô, nhạy cảm, da và lông của chú chó của bạn. Là sản phẩm hoàn hảo cho những chú chó bị ngứa da do dị ứng hoặc da nhạy cảm. Khử mùi và giúp làm giảm nhiễm trùng da do nấm và vi khuẩn như viêm da & viêm da mủ. Hàm lượng chất Pectin cao hơn so với mức bình thường giúp nhanh chóng bổ sung thêm dinh dưỡng cho tế bào lông. Phục hồi hiệu quả sự đàn hồi của lông. Hàm lượng chất xơ Pectin có hiệu quả tối đa bảo vệ bề mặt lông của vật nuôi. Làm cho lông không bị ảnh hưởng bởi những tác động xấu từ môi trường bên ngoài. Giúp cho lông luôn giữ được màu gốc và sáng bóng. Sữa tắm cho chó với thành phần chiết xuất hoa quả, chất béo Jojoba. Vitamin C, A, B2, E và rutin, axit amin tự do. Axit chlorogenic, dầu Glycine, mỡ lông cừu, sợi thô tế bào dưa chuột, vitamin C, đường cam lộ… Tác dụng quan trọng: chống oxy hóa. Bổ sung nước cho lông nhanh chóng. Chiết xuất từ hoa quả nhập khấu chống oxy hóa, chứa nhiều thành phần tự nhiên phenon, Axit tannic và vitamin C, E, lông hấp thụ nhanh nhất. Bổ sung thành phần nước cho lông nhanh nhất.','C001',300)


INSERT [dbo].[Product]  (id, [name], [image], price, title, [description], cateID,quantity) VALUES ('P004',N'Khay vệ sinh cho chó MAKAR Dog Toilet Trays Small','https://www.petmart.vn/wp-content/uploads/2016/07/khay-ve-sinh-cho-cho-makar-dog-toilet-trays-small.jpg','220000',N'Khay vệ sinh cho chó MAKAR Dog Toilet Trays Small với thiết kế thông minh được làm từ 100% nhựa tổng hợp cao cấp an toàn với thú cưng và môi trường. Phù hợp với tất cả các giống chó và giới tính đực cái có trọng lượng dưới 5kg.',N'Khay vệ sinh cho chó MAKAR Dog Toilet Trays Small với tấm lưới lót để nước tiểu không bị vung vãi khắp nhà và làm ướt lông thú cưng. Phần khay lưới bên dưới chắc và không bị ăn mòn. Những chú chó con có thể đứng trên khay để đi vệ sinh dễ dàng và thuận tiện. Phần thành cao chống lại vi khuẩn, côn trùng và ngăn nước tiểu bắn ra ngoài. Đặc biệt, cún con sẽ đánh hơi được dễ dàng và xác định được vị trí để thải ra nhanh hơn. Dễ dàng trong việc hướng dẫn và huấn luyện. Khay vệ sinh cho chó MAKAR Dog Toilet Trays Small có thiết kế nhỏ gọn, đa dạng về kiểu dáng và màu sắc. Bạn có thể lựa chọn gam màu phù hợp với không gian nơi ở của mình. Việc vệ sinh cũng trở lên đơn giản hơn với chất liệu nhựa. Bạn có thể dễ dàng khử mùi và làm sạch chúng. Và yên tâm vì cún cưng sẽ không còn đi vệ sinh bừa bãi nữa.','C001',300)
 
INSERT [dbo].[Product]  (id, [name], [image], price, title, [description], cateID,quantity) VALUES ('P005',N'Thuốc tẩy giun cho chó BAYER Drontal Plus Flavour','https://www.petmart.vn/wp-content/uploads/2017/02/thuoc-tay-giun-cho-cho-bayer-drontal-plus-flavour1.jpg','55000',N'Thuốc tẩy giun cho chó BAYER Drontal Plus Flavour For Dogs điều trị đa nhiễm giun tròn, giun đũa, giun tóc, giun móc và sán dây.',N'Thuốc tẩy giun cho chó BAYER Drontal Plus Flavour For Dogs điều trị đa nhiễm giun tròn, giun đũa, giun tóc, giun móc và sán dây. Giúp tẩy xổ tất cả những loài giun trưởng thành và ấu trùng nguy hiểm trên chó gây co cơ, liệt cơ làm giun không bám được vào vật chủ, còn Febantel cản trở giun sử dụng năng lượng cho các hoạt động làm giun chết nhanh. Giải quyết những vấn đề gây rối loạn tiêu hóa, bỏ ăn, có hiện tượng mệt mỏi do giun quấy phá.','C001',200)

INSERT [dbo].[Product]  (id, [name], [image], price, title, [description], cateID,quantity) VALUES ('P006',N'Chuồng sắt gấp cho chó mèo PAW Collapsible Crate','https://www.petmart.vn/wp-content/uploads/2014/05/chuong-sat-gap-cho-meo-nan-cung-paw-collapsible-crate67.jpg','500000',N'Chuồng sắt gấp cho chó mèo nan cứng PAW Collapsible Crate sơn tĩnh điện là sản phẩm chuồng di động, dễ dàng gấp gọn phù hợp với tất cả giống chó và mèo.',N'Chuồng sắt gấp cho chó mèo nan cứng PAW Collapsible Crate dễ dàng tháo lắp và di chuyển. Có 2 cánh cửa ngang và cửa trên dễ dàng cho thức ăn vào hoặc di chuyển thú cưng.Khay vệ sinh bên dưới hoàn toàn có thể kéo ra để vệ sinh dễ dàng hơn. Cấu trúc chắc chắn, thông thoáng phù hợp cho tất cả các giống chó và mèo. Chuồng sắt cho chó mèo vừa có trọng lượng lại vừa có chất lượng, trọng lượng gấp 2 lần trọng lượng các sản phẩm cùng loại, thiết kế chặt chẽ. Sản phẩm chuyên dùng cho thú cưng, bên ngoài vừa thời trang vừa hữu dụng, còn có thể biến đổi thành hàng rào. Chỉ cần với 1 mức giá mà bạn đã mang về sản phẩm với 2 tính năng. Chuồng sắt cho chó mèo với khoảng cách giữa các nan dày của chuồng là 1cm, khoảng cách như vậy ngăn chặn hiệu quả việc chân thú cưng lọt xuống dưới hoặc bị kẹp lại (thiết kế vô cùng tinh tế). Kỹ thuật hàn tỉ mỉ, chỗ mối hàn nhẵn, đẹp, không gây xước da. Ren chắc, bền, có tác dụng cố định chuồng, để chuồng kiên cố không bị rung lắc. Khi mở cửa thì nâng chuôi then cửa lên thì mới có thể dễ dàng mở cửa. Khi đóng cửa thì để chuôi theo chốc xuống dưới, như thế chú chó nhà bạn mới không mở được cửa. Thiết kế đặt khay nhựa ở dưới cùng, đặt khay ở dưới có thể thuận tiện cho việc làm sạch, khi mà làm sạch chuồng thì khay cũng không dễ bị trơn tuột, làm bẩn khu vực xung quanh.','C006',300)

INSERT [dbo].[Product]  (id, [name], [image], price, title, [description], cateID, quantity) VALUES ('P007',N'Túi đựng chó mèo ANIME hình họa tiết White Cats','https://www.petmart.vn/wp-content/uploads/2016/09/tui-dung-cho-meo-anime-hinh-hoa-tiet-white-cats.jpg','300000',N'Túi đựng chó mèo ANIME hình họa tiết White Cats phù hợp với tất cả giống chó và mèo.',N'Túi đựng chó mèo ANIME hình họa tiết White Cats được dùng khi ra đường, đi chơi rất tiện lợi, an toàn và tạo cảm giác thoải mái cho vật nuôi. Chất liệu túi không thấm nước và dễ làm vệ sinh làm sạch. Dễ dàng tháo gấp gọn lại khi cần. Màu sắc, kích thước & kiểu dáng đa dạng để khách hàng lựa chọn.','C006',200)
