INSERT [dbo].[Product]  (id, [name], [image], price, title, [description], cateID, quantity) VALUES ('P008',N'Thuốc nhỏ gáy trị ve rận cho chó dưới 10kg MERIAL Frontline Plus','https://www.petmart.vn/wp-content/uploads/2013/02/thuoc-nho-gay-tri-ve-ran-cho-cho-duoi-10kg-merial-frontline-plus.jpg','210000',N'Thuốc nhỏ gáy trị ve rận cho chó dưới 10kg MERIAL Frontline Plus. Phù hợp với tất cả các giống chó có trọng lượng dưới 10kg và từ 2 tháng tuổi trở lên. Có thể sử dụng cho chó đang mang thai và nuôi con bú.',N'Thuốc nhỏ gáy trị ve rận cho chó dưới 10kg MERIAL Frontline Plus đáp ứng tất cả nhu cầu đó của bạn. MERIAL Frontline Plus sẽ giúp bạn thật an tâm khi thú cưng sạch rận bọ mà lại an toàn. Đặc biệt, Frontline là thuốc trị rận nhỏ gáy duy nhất có tác dụng tiêu diệt ve rận trên cơ thể chó mèo. Sử dụng MERIAL Frontline Plus để nhỏ vào gáy, ở vị trí đặc biệt này các bé sẽ không thể liếm được thuốc. Lượng thuốc sử dụng rất nhỏ nên không làm lông thú bị ướt nhiều. Thuốc tự lan tỏa ra khắp cơ thể thú cưng sau khi được nhỏ sau đó được thấm và lưu trữ ở lớp mỡ của thú cưng để duy trì tác dụng trong thời gian dài, ngay cả khi bạn tắm, hay cạo lông của bé. Ngoài ra, thuốc nhỏ gáy trị ve rận cho chó dưới 10kg MERIAL Frontline Plus có thể trị được cả trứng rận.','C001',200)
INSERT [dbo].[Product]  (id, [name], [image], price, title, [description], cateID, quantity) VALUES ('P009',N'Lược chải lông chó mèo đầu lưỡi mềm BOBO Pet Comb','https://www.petmart.vn/wp-content/uploads/2016/08/luoc-chai-long-cho-meo-dau-luoi-mem-bobo-pet-comb7.jpg',85000,N'Lược chải lông chó mèo đầu lưỡi mềm BOBO Pet Comb sử dụng cho tất cả các giống cho mèo lông thẳng và lông xoăn.',N'Lược chải lông chó mèo đầu lưỡi mềm BOBO Pet Comb giúp cho thú cưng nhà bạn có bộ lông đẹp ngay tại nhà. Với phần cán bằng inox chắc chắn. Tay cầm vừa vặn có lớp nhựa chống trơn trượt. Bạn có thể dễ dàng sử dụng, chải từng lớp lông rối, rụng của thú cưng một cách hiệu quả. Loại bỏ hầu hết các phần lông chết trên cơ thể của cún cưng. Không gây kích ứng da và cảm giác khó chịu. Phần răng lược mềm mại tạo cảm giác thoải mái nhất cho những người bạn 4 chân.','C006',300)
INSERT [dbo].[Product]  (id, [name], [image], price, title, [description], cateID, quantity) VALUES ('P010',N'Cát vệ sinh hữu cơ cho mèo CATS BEST Original','https://www.petmart.vn/wp-content/uploads/2020/12/cats-best-original.jpg',250000,N'Cát vệ sinh hữu cơ cho mèo CATS BEST Original vón cục, thấm hút chất lỏng gấp 7 lần thể tích cát, khóa chặt mùi hôi và vi khuẩn trong một thời gian dài nhờ hệ thống mao dẫn của sợi cây, giải pháp tiết kiệm và an toàn cho cả người, mèo và môi trường.',N'Cát vệ sinh cho mèo hữu cơ CATS BEST Original – Khóa chặt mùi hôi và vi trùng. Công nghệ JRS-ORGANIC FIBRE700 có chức năng như một chiếc bẫy mùi hôi và vi trùng, nhanh chóng thấm hút chất lỏng và mùi hôi, khóa chúng lại trong một thời gian dài nhờ hệ thống mao dẫn siêu tốt của sợi cây. Nhờ vậy, mùi hôi không bị phát tán ra ngoài mà không cần dùng bất kỳ hóa chất nào.','C002',300)
INSERT [dbo].[Product]  (id, [name], [image], price, title, [description], cateID, quantity) VALUES ('P011',N'Bộ kìm cắt móng chó mèo kèm mài dũa PAW For Large Pet Nail Trimmer','https://www.petmart.vn/wp-content/uploads/2023/06/bo-kim-cat-mong-cho-meo-kem-mai-dua-paw-for-large-pet-nail-trimmer.jpg',115000,N'Bộ kìm cắt móng chó mèo kèm mài dũa PAW For Large Pet Nail Trimmer',N'Bộ kìm cắt móng chó mèo kèm mài dũa PAW For Large Pet Nail Trimmer','C006',100)
INSERT [dbo].[Product]  (id, [name], [image], price, title, [description], cateID, quantity) VALUES ('P012',N'Nhà cây cho mèo Cat Tree CT005','https://www.petmart.vn/wp-content/uploads/2019/04/nha-cay-cho-meo-cat-tree-ct005.jpg',5800000,N'Nhà cây cho mèo Cat Tree CT005 đơn giản mà tinh tế với chân đế vững chắc kết hợp với một trụ ổ kín đáo và cột cào móng sẽ giúp cho chú mèo của ban có đầy đủ nhất những điều kiện cần thiết giúp chúng cảm thấy yên tâm và vui vẻ hơn. Sản phẩm có thể tháo lắp để vệ sinh dễ dàng, tiện lợi.',N'Nhà cây cho mèo Cat Tree CT005 là món đồ chơi, giường ổ nệm có nhiều lợi ích khác nhau. Phục vụ cho bản năng tự nhiên của mèo. Được thiết kế để trở thành một chung cư cao cấp tạo cảm giác an toàn. Với các lỗ hình khối và trụ cào móng có độ cao khác nhau để cho phép thú cưng của bạn nhảy nhót và vui đùa, nghỉ ngơi ngay tại vị trí. Có thể giúp giữ thú cưng không cắn phá đồ nội thất đắt tiền của bạn. Trụ cào an toàn, không gây xước và tổn thương tới bộ móng của mèo. Có rất nhiều tầng xếp để mèo cưng có thể chơi đùa cả ngày mà không biết chán. Kích thước: 95 x 65 x 200cm. Trọng lượng: 15kg.','C002',50)

